import React, { useState } from 'react';

import './Expense.css';
import './ExpensesFilter.css';
import ExpensesList from './ExpensesList';
import ExpenseFilter from './ExpensesFilter';

import Card from './../UI/Card'

const Expenses = (props) => {


  const [items, setItems] = useState(props.items)
  const [filteredYear, setFilteredYear] = useState();
  const filterHandler = (selectedYear) => {
    setFilteredYear(selectedYear)
    const filteredExpense = [];
    if (selectedYear === '0') {
      filteredExpense = props.items;
    }
    else 
    {
        filteredExpense = props.items.filter((item) => {
        let year = item.date.getFullYear()
        console.log(year + " " + selectedYear);
        if (year === selectedYear) {
          console.log("matched");
        }
        return item.date.getFullYear() == selectedYear;
        })
      console.log(props.items);
    }
    setItems(filteredExpense);

  }

  return (
    <div>
      <Card className="expenses">
        <ExpenseFilter selected={filteredYear} className='expenses-filter'
          onChangeFilter={filterHandler}>
        </ExpenseFilter>
        <ExpensesList items={items} />
      </Card>
    </div>
  );
}

export default Expenses;
